SELECT adherent.nomAdherent, etat.libelleEtat, etat.id AS idEtat, typeService.libelleService, typeService.prix, serviceDemande.dateDemande, serviceDemande.id AS idServiceDemande, adherent.id AS Expr1, etat.id AS Expr2, typeService.id AS Expr3 FROM serviceDemande INNER JOIN adherent ON serviceDemande.idAdherent = adherent.id INNER JOIN etat ON serviceDemande.idEtat = etat.id INNER JOIN typeService ON serviceDemande.idService = typeService.id